import { Typography, Image, Row, Col, Layout, Rate } from 'antd';
import React, { Component, useEffect, useState } from 'react'
import { connect } from 'react-redux';
import movieActions from '../store/actions/MovieActions'
import {
    CalendarOutlined,
    ClockCircleOutlined
} from '@ant-design/icons';

const { Title, Text } = Typography;
const { movieFetchById } = movieActions;
const { Content } = Layout;



function MovieDetail({...props}) {
    useEffect(()=>{
        const imdbID = props.match.params.imdbID;
        if (!imdbID) {
            props.history.push("/error")
        } else {
            props.movieFetchById(imdbID)
        }
        console.log(props.movie)
    },[])
    return (
        <Layout style={{ minHeight: '100vh', backgrounColor: "#0093E9", backgroundImage: "linear-gradient(160deg, #0093E9 0%, #80D0C7 50%)" }}>
            <Content className="site-layout" style={{ padding: '0 50px', marginTop: 64 }}>
                <Row>
                    <Col span={6}>
                        <Image
                            width={"95%"}
                            height={"60vh"}
                            src={props.movie.Poster}
                            fallback="data:image/png;base64,"
                        />
                    </Col>
                    <Col span={18} style={{ marginTop: "7%" }}>
                        <Row>
                            <Title level={2}>{props.movie.Title} ({props.movie.Type})</Title>
                        </Row>
                        <Row >
                            <Text style={{ fontSize: "18px" }}>{props.movie.Language}</Text>
                        </Row>
                        <Row style={{ marginTop: "16px" }}>
                            <Col span={4}><CalendarOutlined /> <Text strong style={{ fontSize: "16px" }} >{props.movie.Year}</Text> </Col>
                            <Col span={4}><ClockCircleOutlined /> <Text strong style={{ fontSize: "16px" }} >{props.movie.Runtime} </Text></Col>
                            <Col span={4}>
                                <Rate disabled allowHalf value={(props.movie.imdbRating) / 2} />
                                <Text strong style={{ fontSize: "16px" }}>{props.movie.imdbRating}</Text>
                            </Col>
                        </Row>
                        <Row style={{ marginTop: "12px" }}>
                            <Text style={{ fontSize: "24px" }} >{props.movie.Genre}</Text>
                        </Row>
                        <Row style={{ marginTop: "16px" }}>
                            <Text style={{ fontSize: "18px" }} strong>Director: </Text>
                            <Text style={{ fontSize: "18px" }}>{props.movie.Director}</Text>
                        </Row>
                    </Col>





                </Row>
            </Content>
        </Layout>
    )
}



const mapStateToProps = state => {
    const { movie } = state.movie;
    return {
        movie,
    }
};

export default connect(mapStateToProps, { movieFetchById })(MovieDetail);